import React, { Component } from "react";

class RestaurantCreate extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: null,
      rating: null,
      address: null,
      email: null,
    };
  }
create()
{
    console.warn(this.state)
   // console.alert("Data insert")
}
  render() {
    return (
      <div>
        <h1>Restaurant Create</h1>
        <div>
          <input
            onChange={(event) => {
              this.setState({
                name: event.target.value,
              });
            }}
            placeholder="Enter Restraurant name"
          />
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                rating: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Rating"
          />
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                address: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Location"
          />
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                email: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Email"
          />
          <br />
          <br />
          <button onClick={()=>{this.create()}}>Submit</button>
        </div>
      </div>
    );
  }
}
export default RestaurantCreate;
