import React, { Component } from "react";
//import { Table } from "react-bootstrap";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit,faTrashAlt } from '@fortawesome/free-solid-svg-icons'

class RestaurantSearch extends Component {
  constructor(props) {
    super(props)

    this.state = {
       searchData:null,
       noData:false
    }
  }
search(key)
{
  console.warn(key)
fetch("http://localhost:3000/restaurant?q="+key).then((data)=>{
data.json().then((resp)=>{
  console.warn("Respo..",resp)
  if(resp.length>0)
  {
    this.setState({searchData:resp,noData:false});
  }
else
{
  this.setState({noData:true,searchData:null}); 
}

})

})

  }


  render() {
    return (
      <div>
        <h1>Restaurant Search</h1><br />
       <input type="text" onChange={(event)=>this.search(event.target.value)}/> 
 <div>
{
this.state.searchData?
<div className="row-search">
<Table striped bordered hover>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Rating</th>
                  <th>Location</th>
                  <th>Email</th>
                  <th>Update</th>
                </tr>
              </thead>
              <tbody>
{
this.state.searchData.map((item)=><div>()
   <tr>
<td><b>{item.id}</b></td>
<td><b>{item.name}</b></td>
<td><b>{item.rating}</b></td>
<td><b>{item.address}</b></td>
<td><b>{item.email}</b></td>
<td><Link to={"/update/"+item.id}><FontAwesomeIcon icon={faEdit} color="orange"/></Link>
<span onClick={()=>this.delete(item.id)}><FontAwesomeIcon icon={faTrashAlt} color="red"/></span>
</td>

</tr>
</div>)

}
</tbody>
</Table>
  </div>
:''

}
{
  this.state.noData? <h3>No data found</h3>:null
}
</div> 


      </div>
    );
  }
}
export default RestaurantSearch;
