import React, { Component } from "react";

class RestaurantUpdate extends Component {
  constructor(props) {
    super(props)
    this.state = {
      name: null,
      rating: null,
      address: null,
      email: null,
      id:null
    };
  }
  componentDidMount() {
fetch('http://localhost:3000/restaurant/'+this.props.match.params.id).then((response) => {
  response.json().then((result) => {
    this.setState({ 
      name:result.name, 
      email:result.email, 
      // :result.name, 
      // name:result.name, 

    })
  });
});

  }
  render() {
   console.warn(this.state)
    return (
      <div>
        <h1>Restaurant Update</h1>
        <div>
          <input
            onChange={(event) => {this.setState({ name: event.target.value, });
            }} placeholder="Enter Restraurant name" value={this.state.name}/>
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                rating: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Rating"
          />
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                address: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Location"
          />
          <br />
          <br />
          <input
            onChange={(event) => {
              this.setState({
                email: event.target.value,
              });
            }}
            placeholder="Enter Restraurant Email"
          />
          <br />
          <br />
          <button onClick={()=>{this.update()}}>Update Restaurant</button>
        </div> 
      </div>
    );
  }
}
export default RestaurantUpdate;
